mapper.insert:

    SELECT NVL(MAX(CAST(item_code AS UNSIGNED))+1, '1000000001') FROM RENTAL_ITEM


INSERT INTO RENTAL_ITEM (
    item_code
    , item_name
    , item_number
    , have_number
    , user_id
    , memo
    , use_yn
    , reg_id
    , reg_date
)
VALUES (
    #{itemCode}
   , #{itemName}
   , #{itemNumber}
   , #{itemNumber}
   , #{userId}
   , #{memo}
   , #{useYn}
   , #{regId}
   , NOW()
)

mapper.update:
UPDATE RENTAL_ITEM
SET item_name = #{itemName}
  , item_number = #{itemNumber}
  , use_yn = #{useYn}
  , mod_id = #{regId}
  , mod_date = NOW()

WHERE item_code =  #{itemCode}

mapper.insert:

    SELECT NVL(MAX(CAST(item_code AS UNSIGNED))+1, 'R000000001') FROM RENTAL_LOG


INSERT INTO RENTAL_LOG (
    rt_code
    , item_code
    , user_id
    , rt_state
    , rt_number
    , borrow_date
    , return_date
    , rt_days
    , return_yn
    , reg_id
    , reg_date
)
VALUES (
    #{rtCode}
    , #{itemCode}
    , #{userId}
    , #{rtState}
    , #{rtNumber}
    , #{borrowDate}
    , #{returnDate}
    , #{rtDays}
    , #{returnYn}
    , #{regId}
    , NOW()
)

mapper.update:

    SELECT CAST(have_number AS UNSIGNED)-1 AS haveNumber FROM RENTAL_ITEM WHERE item_code =  #{itemCode}


UPDATE RENTAL_ITEM
SET have_number = #{haveNumber}
  , mod_id = #{regId}
  , mod_date = NOW()

WHERE item_code =  #{itemCode}
